  (() => {
    const fechaOriginal = $json.body.fechaHora;

    // Separar fecha y hora
    const [fecha, horaCompleta] = fechaOriginal.split(", ");
    const [dia, mes, anio] = fecha.split("/");

    // Detectar si es "p. m." y convertir a 24 hrs
    let [hora, minutos, segundos] = horaCompleta
      .replace("p. m.", "PM")
      .replace("a. m.", "AM")
      .trim()
      .split(/[: ]/);

    hora = parseInt(hora, 10);
    if (horaCompleta.includes("PM") && hora < 12) hora += 12;
    if (horaCompleta.includes("AM") && hora === 12) hora = 0;

    const horaFormateada = String(hora).padStart(2, "0");
    const fechaSQL = `${anio}-${mes.padStart(2, '0')}-${dia.padStart(2, '0')} ${horaFormateada}:${minutos}:${segundos}`;

    return fechaSQL;
  })()
}}

